import React from 'react';
import {Button} from './Button';
import './HeroSection.css';
import '../App.css';

function HeroSection() {
  return (
    <div className='hero-container'>
        <video src ="../public/videos/video-2.mp4" autoPlay loop muted />
        <h1>ADVENTURE AWAITS</h1>
        <p>What are you waiting for?</p>
        <div className="hero-btns">
            <Button className="btns" buttonStyle="btn--outline" buttonSize='bton--large'> GET STARTED</Button>
            <Button className="btns" buttonStyle="btn--primary" buttonSize='bton--large'> Watch Trailer <i className='far fa-play-circle'/> </Button>

        </div>

    </div>
  )
}

export default HeroSection
